'use strict';

var _ochreVisionmaker = require('ochre-visionmaker');

var lang = process.env.FILEJS_LANG || 'eng';

function extractText(file, cb) {
    (0, _ochreVisionmaker.getText)(file, lang, function (err, contents) {
        cb(err, { contents: contents });
    });
}

module.exports = {
    resolve: extractText,
    types: ['.jpg', '.jpeg', '.bmp', '.tif', '.tiff', '.png']
};